import React, { useState, useEffect } from 'react';
import CosmicAvatar from './components/CosmicAvatar';
import ConsciousMode from './components/ConsciousMode';
import GroupSession from './components/GroupSession';
import ProgressBar from './components/ProgressBar';
import './App.css';

function App() {
  const [userId, setUserId] = useState(1);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/progress', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_id: userId })
    })
      .then(res => res.json())
      .then(data => setProgress(data.progress));
  }, [userId]);

  return (
    <div className="app">
      <div className="header">
        <div className="awareness-orb"></div>
        <h1>ConsciousApp</h1>
      </div>
      <CosmicAvatar progress={progress} />
      <ProgressBar progress={progress} />
      <ConsciousMode userId={userId} setProgress={setProgress} />
      <GroupSession userId={userId} />
    </div>
  );
}

export default App;
